   531	static u32 gf_dash_group_count_rep_needed(GF_DASH_Group *group)
   532	{
   533		u32 count, nb_rep_need, next_rep_index_plus_one;
   534		GF_MPD_Representation *rep;
   535		count  = gf_list_count(group->adaptation_set->representations);
   536		nb_rep_need = 1;
   537		if (!group->base_rep_index_plus_one || (group->base_rep_index_plus_one == group->max_complementary_rep_index+1))
   538			return nb_rep_need; // we need to download only one representation
   539		rep = gf_list_get(group->adaptation_set->representations, group->base_rep_index_plus_one-1);
   540		next_rep_index_plus_one = rep->playback.enhancement_rep_index_plus_one;
   541		while ((nb_rep_need < count) && rep->playback.enhancement_rep_index_plus_one) {
   542			nb_rep_need++;
   543			if (next_rep_index_plus_one == group->max_complementary_rep_index+1)
   544				break;
   545			rep = gf_list_get(group->adaptation_set->representations, next_rep_index_plus_one-1);
   546			next_rep_index_plus_one = rep->playback.enhancement_rep_index_plus_one;
   547		}
   548	
   549		gf_assert(nb_rep_need <= count);
   550	
   551		return nb_rep_need;
   552	}
