                                GF_LOG(GF_LOG_ERROR, GF_LOG_CONTAINER, ("[iso file] Error: TRAF box of track id=%u list less samples than SDTP.\n", traf->tfhd->trackID));
                        }
                        gf_isom_box_del((GF_Box*)traf->sdtp);
                        traf->sdtp = NULL;
                }
        }
